mapper.select:
SELECT COUNT(DISTINCT a.rid,a.tid)FROM re_raceteammember a
LEFT JOIN tm_team b ON b.tid = a.tid
WHERE b.signuid=#{signuid} AND b.tstatus='00' AND a.mid=#{mid}

SELECT COUNT(DISTINCT a.rid,a.tid) FROM re_raceteammember a 
LEFT JOIN tm_team b ON b.tid = a.tid
LEFT JOIN os_user c ON c.uid = b.signuid
WHERE b.tstatus='00' and a.mid=#{mid}

		 AND a.rid=#{rid}
 

		 AND b.tname LIKE '%${tname}%'
 

		 AND b.orgtype =#{orgtype}
 

		 AND b.region= #{region}
 

		 AND b.school LIKE '%${school}%'
 

		 AND b.ckstatus= #{ckstatus}
 

		 AND a.feestatus= #{feestatus}
 

		 AND a.infostatus =#{infostatus}
 

		 AND a.tcode LIKE '%${tcode}%'

SELECT b.*,a.infostatus,a.feestatus,a.feedback,c.rid,c.rname,d.mname,e.paymenturl,a.tcode as rtcode FROM re_raceteammember a 
LEFT JOIN tm_team b ON b.tid = a.tid
LEFT JOIN ma_race c ON c.rid = a.rid
left join ma_match d on d.mid=a.mid
LEFT JOIN os_user e ON e.uid=a.signuid
WHERE b.tstatus='00' and a.mid=#{mid}

		 AND a.rid=#{rid}
 

		 AND b.tname LIKE '%${tname}%'
 

		 AND b.orgtype= #{orgtype}
 

		 AND b.region= #{region}
 

		 AND b.school LIKE '%${school}%'
 

		 AND b.ckstatus= #{ckstatus}
 	

		 AND a.feestatus= #{feestatus}
 

		 AND a.infostatus =#{infostatus}


		 AND a.tcode LIKE '%${tcode}%'
 
GROUP BY a.rid,a.tid
ORDER BY b.createdate,c.createdate


		limit #{beginNo}, #{endNo}

mapper.update:
	 update re_raceteammember set infostatus = #{infostatus},
        feedback = #{feedback},  upduid = #{upduid},     
        upddate = #{upddate},  reviewuid = #{reviewuid},
        reviewdate = #{reviewdate}
		 where tid = #{tid} AND rid=#{rid}

mapper.select:
SELECT DISTINCT a.rid,a.rname,b.infostatus,b.tcode FROM ma_race a
LEFT JOIN re_raceteammember b ON a.rid=b.rid
WHERE b.tid=#{0}

SELECT DISTINCT a.rid,a.rname,b.infostatus,b.tcode FROM ma_race a
LEFT JOIN re_raceteammember b ON a.rid=b.rid
WHERE b.tid=#{0} and a.rid=#{1}

SELECT * FROM ( SELECT GROUP_CONCAT(b.tcode) as tcode,a.tname,a.school,a.orgtype,a.region ,a.uname,a.phone,a.email,a.invoicename,a.taxpayernumber,a.banknumber,a.addressphone,a.receiveaddress ,a.tchname,a.tchsex,a.tchdidtype,a.tchdid,a.tchemail,a.tchphone,a.tchfolk,a.tchdiningtype ,a.stuname,a.stusex,a.studidtype,a.studid,a.stuemail,a.stuphone,a.stufolk,a.studiningtype ,LENGTH(a.tchname)-LENGTH(REPLACE(a.tchname,',',''))+1 AS tchnum ,LENGTH(a.stuname)-LENGTH(REPLACE(a.stuname,',',''))+1 AS stunum ,GROUP_CONCAT(b.infostatus) AS infostatus,GROUP_CONCAT(b.feestatus) AS feestatus ,GROUP_CONCAT(c.frname) AS frname,GROUP_CONCAT(c.rcode) AS rcode,GROUP_CONCAT(c.rname) AS rname FROM( SELECT b.tname,b.school,b.orgtype,b.region ,c.uname,c.phone,c.email,c.invoicename,c.taxpayernumber,c.banknumber,c.addressphone,c.receiveaddress ,GROUP_CONCAT( d.tmname order by a.serialnum) AS tchname,GROUP_CONCAT( d.sex order by a.serialnum) AS tchsex,GROUP_CONCAT( d.didtype order by a.serialnum) AS tchdidtype ,GROUP_CONCAT( d.did order by a.serialnum) AS tchdid,GROUP_CONCAT( d.email order by a.serialnum) AS tchemail,GROUP_CONCAT( d.phone order by a.serialnum) AS tchphone ,GROUP_CONCAT( f.folk order by a.serialnum) AS tchfolk,GROUP_CONCAT( d.diningtype order by a.serialnum) AS tchdiningtype
,GROUP_CONCAT( e.tmname order by a.serialnum) AS stuname,GROUP_CONCAT( e.sex order by a.serialnum) AS stusex,GROUP_CONCAT( e.didtype order by a.serialnum) AS studidtype ,GROUP_CONCAT( e.did order by a.serialnum) AS studid,GROUP_CONCAT( e.email order by a.serialnum) AS stuemail,GROUP_CONCAT( e.phone order by a.serialnum) AS stuphone ,GROUP_CONCAT( g.folk order by a.serialnum) AS stufolk,GROUP_CONCAT( e.diningtype order by a.serialnum) AS studiningtype ,a.tid,GROUP_CONCAT(a.tmid order by a.serialnum) AS tmid FROM ( SELECT DISTINCT a.tid,a.tmid,a.serialnum FROM re_raceteammember a WHERE a.mid=#{mid} ) a LEFT JOIN tm_team b ON b.tid = a.tid LEFT JOIN os_user c ON c.uid = b.signuid LEFT JOIN tm_member d ON d.tmid = a.tmid AND d.roleflg = '01' LEFT JOIN tm_member e ON e.tmid = a.tmid AND e.roleflg = '02' LEFT JOIN sys_folk f ON f.folkid = d.folk LEFT JOIN sys_folk g ON g.folkid = e.folk GROUP BY a.tid ) a LEFT JOIN ( SELECT DISTINCT a.tid,a.rid,a.infostatus,a.feestatus,a.tcode FROM re_raceteammember a WHERE a.mid=#{mid} ) b ON b.tid = a.tid LEFT JOIN ma_race c ON c.rid = b.rid GROUP BY a.tid ) a WHERE 1=1
AND a.tcode like '%${tcode}%'
AND a.tname=#{tname}
AND a.school=#{school}
AND a.orgtype=#{orgtype}
AND a.region=#{region}
AND a.uname=#{uname}
AND a.email=#{email}

SELECT * FROM ( SELECT b.tmid,b.tmname,b.roleflg,b.sex,b.didtype,b.did,b.email,b.phone,g.folk,b.diningtype
,GROUP_CONCAT(c.tid) AS tid,GROUP_CONCAT(a.tcode) AS tcode,GROUP_CONCAT(c.tname) AS tname ,GROUP_CONCAT(DISTINCT c.school) AS school,GROUP_CONCAT(DISTINCT c.orgtype) AS orgtype,GROUP_CONCAT( DISTINCT c.region) AS region ,GROUP_CONCAT( DISTINCT CONCAT( '(', f.tchname,')')) AS tchname
,GROUP_CONCAT( DISTINCT e.uname order by convert(e.uname using gbk) asc) AS uname,GROUP_CONCAT(DISTINCT e.phone) AS uphone,GROUP_CONCAT(DISTINCT e.email) AS uemail ,GROUP_CONCAT(DISTINCT e.invoicename) AS invoicename,GROUP_CONCAT(DISTINCT e.taxpayernumber) AS taxpayernumber,GROUP_CONCAT(DISTINCT e.banknumber) AS banknumber ,GROUP_CONCAT(DISTINCT e.addressphone) AS addressphone,GROUP_CONCAT(DISTINCT e.receiveaddress) AS receiveaddress
,GROUP_CONCAT(d.rid) AS rid,GROUP_CONCAT(DISTINCT d.frname) AS frname,GROUP_CONCAT(d.rcode) AS rcode ,GROUP_CONCAT(d.rname) AS rname
,GROUP_CONCAT(a.infostatus) AS infostatus,GROUP_CONCAT(DISTINCT a.feestatus) AS feestatus FROM re_raceteammember a LEFT JOIN tm_member b ON b.tmid = a.tmid LEFT JOIN tm_team c ON c.tid = a.tid LEFT JOIN ma_race d ON d.rid = a.rid LEFT JOIN os_user e ON e.uid = a.signuid LEFT JOIN sys_folk g ON g.folkid = b.folk LEFT JOIN ( SELECT a.tid,GROUP_CONCAT(b.tmname) AS tchname FROM ( SELECT DISTINCT a.tid,a.tmid FROM re_raceteammember a WHERE a.mid=#{mid} ) a LEFT JOIN tm_member b ON b.tmid = a.tmid WHERE b.roleflg='01' GROUP BY a.tid ) f ON f.tid = c.tid WHERE a.mid=#{mid} GROUP BY a.tmid ) a WHERE 1=1
AND a.tmname=#{tmname}
AND a.roleflg=#{roleflg}
AND a.sex=#{sex}
AND a.didtype=#{didtype}
AND a.did=#{did}
AND a.diningtype=#{diningtype}
ORDER BY a.region,a.school,convert(a.uname using gbk) asc,a.tid,a.tmid,a.rid

SELECT 
b.frname,b.rcode,b.rname
,a.tcode,c.tname,c.school,c.orgtype,c.region
,d.uname,d.phone,d.email,d.invoicename,d.taxpayernumber,d.banknumber,d.addressphone,d.receiveaddress
,e.tchname,e.tchsex,e.tchdidtype,e.tchdid,e.tchphone,e.tchemail,e.diningtype,e.stuname,e.stunum
,a.infostatus,a.feestatus,a.createdate,c.ckstatus
FROM (
	SELECT DISTINCT a.tid,a.rid,a.signuid,a.infostatus,a.feestatus,a.createdate,a.tcode FROM re_raceteammember a
	WHERE a.mid=#{mid}
) a
LEFT JOIN ma_race b ON b.rid = a.rid
LEFT JOIN tm_team c ON c.tid = a.tid
LEFT JOIN os_user d ON d.uid = a.signuid
LEFT JOIN (
			SELECT
			a.tid,GROUP_CONCAT(b.tmname order by a.serialnum) AS tchname,GROUP_CONCAT(b.sex order by a.serialnum) AS tchsex,GROUP_CONCAT(b.didtype order by a.serialnum) AS tchdidtype
			,GROUP_CONCAT(b.did order by a.serialnum) AS tchdid,GROUP_CONCAT(b.phone order by a.serialnum) AS tchphone,GROUP_CONCAT(b.email order by a.serialnum) AS tchemail,
			GROUP_CONCAT(b.diningtype order by a.serialnum) AS diningtype,
			GROUP_CONCAT( c.tmname order by a.serialnum) AS stuname, COUNT(c.tmname) AS stunum
			FROM (
				SELECT DISTINCT a.tid,a.tmid,a.serialnum FROM re_raceteammember a
				WHERE a.mid=#{mid}
			) a 
			LEFT JOIN tm_member b ON b.tmid = a.tmid AND b.roleflg='01'
			LEFT JOIN tm_member c ON c.tmid = a.tmid AND c.roleflg='02'
			GROUP BY a.tid
) e ON e.tid = a.tid
WHERE 1=1 

AND b.frname=#{frname} 


AND b.rname=#{rname}


AND c.tname=#{tname}


AND a.tcode=#{tcode}


AND c.school=#{school}


AND c.orgtype=#{orgtype}


AND c.region=#{region}


AND d.uname=#{uname}


AND d.email=#{email}


AND a.infostatus=#{infostatus}


AND c.ckstatus=#{ckstatus}
		
ORDER BY a.rid,a.tid

SELECT COUNT(*) FROM re_raceteammember a 
left join tm_member b on a.tmid=b.tmid
where a.tid=#{1} and a.serialnum=#{0} and b.roleflg=#{2}

SELECT DISTINCT a.* FROM  ma_match a
LEFT JOIN re_raceteammember b ON a.mid=b.mid
where b.signuid=#{0}
